import React, { useContext } from 'react'
import './MainLogin.css';
import { useNavigate } from "react-router-dom";
import { UserContext } from './UserContext';
const axios = require('axios')

function MainLogin () {
  const {user,setUser} = useContext(UserContext)

  const [login,setLogin] = useState('');
  const [mdp,setMdp] = useState('');

  let navigate = useNavigate(); 
  const routeChangeLogin = async () =>{ 
      axios.post("http://localhost:8999/user/login", {
        login : login ,
        password : mdp
      })
        .then((resp) => {
          setUser("helloo")
          let path = "/"; 
          navigate(path);
        })
        .catch((err) => {
          setUser("helloo")
          let path = "/"; 
          navigate(path);
        })
      
  }

  return (
    <div className="mainLogin">


        <h2 className='top'>UserName</h2>
        <input placeholder="Felix75"  type="text" ></input>

        <h2>Password</h2>
        <input placeholder="123"  type="password" ></input>

        {user ? (
          <button>DÃ©connexion</button>
        ) : (
        <button className="btnLogin" onClick={async () => {
            const user = await routeChangeLogin();
            setUser(user);
          }
        }>
          Connexion
        </button>
        )}
        
    </div>
  )
}

export default MainLogin;
