import React, { useContext, useState } from "react";
import "./Tweet.css";
import {Avatar} from "@material-ui/core";
import {AiFillSave,AiFillLike,AiFillDislike} from 'react-icons/ai'
import Friend from "./Friend.js"
import { UserContext } from "./UserContext";


const axios = require('axios')


function Tweet() {
    /* props : userName, text, avatar, image */

    const {user,setUser} = useContext(UserContext)

    const [messages,setMessages] = useState([]);
    const [avaa, setAvaa] = useState("");


    const componentDidMount = () => {
        axios.get("http://localhost:4000/messages")
        .then(res => {
            const messages = res.data;
            setMessages({ messages });
        })
    }

    const getAvatar = () => {
        axios.get("http://localhost:4000/getUserAvatar/" + user)
        .then(res => {
            setAvaa(res.data)
            return res.data
        })
    }

    const ava = getAvatar()
    const comp = componentDidMount()

    return (
        messages.map(message =>
            <div className="Tweet">
                <div className="header">
                    <Friend login={message.login} avatar="https://static.actu.fr/uploads/2021/10/portrait-jean-lassalle-gl-actu-toulouse-2.jpg"/>
                </div>
                <div className="text">
                    {message.texte}
                </div>
                <div className="image">
                    <img src={message.imgUrl} />
                </div>
                <div className="footer">
                    <button className="btn1">
                        <AiFillLike color="#1DA1F2" fontSize="2rem" />
                    </button>
                    <div>
                        {message.likes}
                    </div>
                    <button className="btn1">
                        <AiFillDislike color="#1DA1F2" fontSize="2rem" />
                    </button>
                    <div>
                        {message.dislikes}
                    </div>
                    <button className="btn1">
                        <AiFillSave color="#1DA1F2" fontSize="2rem" />
                    </button>
                </div>
            </div>
        )
    )
    
    
}
export default Tweet
