import React, { useState } from "react";
import "./Tweet.css";
import {Avatar} from "@material-ui/core";
import {AiFillSave,AiFillLike,AiFillDislike} from 'react-icons/ai'
import Friend from "./Friend.js"

const axios = require('axios')


function Tweet() {
    /* props : userName, text, avatar, image */

    const [messages,setmessages] = useState([]);


    const componentDidMount = () => {
        axios.get("http://localhost:4000/messages")
        .then(res => {
            const messages = res.data;
            setmessages(messages);
            console.log(res.data)
        })
    }
    const comp = componentDidMount()


    const getAva = (login) => {
        axios.get("http://localhost:4000/getUserAvatar/" + login)
        .then(res => {
            return res.data
        })
    }

    return (
        this.state.messages.map(message =>
            <div className="Tweet">
                <div className="header">
                    <Friend login={message.login} avatar={getAva(message.login)}/>
                </div>
                <div className="text">
                    {message.texte}
                </div>
                <div className="image">
                    <img src={message.imgUrl} />
                </div>
                <div className="footer">
                    <button className="btn1">
                        <AiFillLike color="#1DA1F2" fontSize="2rem" />
                    </button>
                    <div>
                        {message.likes}
                    </div>
                    <button className="btn1">
                        <AiFillDislike color="#1DA1F2" fontSize="2rem" />
                    </button>
                    <div>
                        {message.dislikes}
                    </div>
                    <button className="btn1">
                        <AiFillSave color="#1DA1F2" fontSize="2rem" />
                    </button>
                </div>
            </div>
        )
    )
    
}
export default Tweet
