import React, { useContext, useState } from "react";
import "./ProfileMain.css"
import TweetBox from "./TweetBox"
import Tweet from "./Tweet"
import Tabs from "./Tabs";
import InfoPerso from "./InfoPerso"
import Friend from "./Friend.js"
import axios from "axios";
import { UserContext } from "./UserContext";

function ProfileMain(props) {

    const {user,setUser} = useContext(UserContext)
    
    
    const [avaa, setAvaa] = useState("");
    const getAvatar = () => {
        axios.get("http://localhost:4000/getUserAvatar/" + user)
        .then(res => {
            setAvaa(res.data)
            return res.data
        })
    }
    const ava = getAvatar()

    const [nbM, setNbM] = useState("");
    const getNbMessage = () => {
        axios.get("http://localhost:4000/getUserNombrebMessage/" + user)
        .then(res => {
            setNbM(res.data)
            return res.data
        })
    }
    const nbbb = getAvatar()


    return (
        <div className="ProfileMain">
            <div className="sticky">
                <TweetBox />
            </div>
            {/* Header */}
        
            <Tabs className="sticky">
                <div label="Informations Personnelles">
                    <InfoPerso login={props.user} avatar={props.avaa} nbMe={nbM}/>
                </div>

                <div label="Messages">
                    <Tweet />
                    
