        return (
            <div className="ProfileMain">
    
    
                    <div className="bor">
                        <InfoPerso login={props.login} avatar={props.avatar} nbMe={nbM} />
                        <input 
                        className="delete-button"
                        type='button'
                        value='Ajouter en ami'
