import React from "react";
import "./Tweet.css";
import {Avatar} from "@material-ui/core";
import {AiFillSave,AiFillLike,AiFillDislike} from 'react-icons/ai'
import Friend from "./Friend.js"

const axios = require('axios')


class Tweet extends React.Component {
    /* props : userName, text, avatar, image */

    state = {
        messages: []
    }

    componentDidMount() {
        axios.get("http://localhost:4000/messages")
        .then(res => {
            const messagess = res.data;
            const messageModif = messages.map(message => {
                const  { login, texte, imgUrl, likes, dislikes, _id, createdAt, updatedAt } = messages
                const ava = axios.get("http://localhost:4000/getUserAvatar/" + login)
                .then(res => {
                    return res.data
                })
                const messagess = { login, texte, imgUrl, likes, dislikes, _id, createdAt, updatedAt,ava }
            })
            
            this.setState({ messages });
            console.log(res.data)
        })
    }

    


    render() {
        return (
            this.state.messages.map(message =>
                <div className="Tweet">
                    <div className="header">
                        <Friend login={message.login} avatar={message.ava}/>
                    </div>
                    <div className="text">
                        {message.texte}
                    </div>
                    <div className="image">
                        <img src={message.imgUrl} />
                    </div>
                    <div className="footer">
                        <button className="btn1">
                            <AiFillLike color="#1DA1F2" fontSize="2rem" />
                        </button>
                        <div>
                            {message.likes}
                        </div>
                        <button className="btn1">
                            <AiFillDislike color="#1DA1F2" fontSize="2rem" />
                        </button>
                        <div>
                            {message.dislikes}
                        </div>
                        <button className="btn1">
                            <AiFillSave color="#1DA1F2" fontSize="2rem" />
                        </button>
                    </div>
                </div>
            )
        )
    }
    
}
export default Tweet
